#test json#import json#a=json.loads('{\"animals\": {\"dog\": [{\"name\": \"Rufus\",\"age\":15},{\"name\": \"Marty\",\"age\": null}]}')from mqtt import OneNetMQTTfrom machine import I2C,Pin,WDTimport time# I2C i2c = I2C(scl=Pin(14),sda=Pin(2),freq=100000)# device addri2c_addr = 0x68#data = b'\x01'i2c.writeto_mem(i2c_addr, 0x6B, data)data = b'\x00'i2c.writeto_mem(i2c_addr, 0x6C, data)g_switch=1p2=Pin(2, mode=Pin.OUT, pull=None, value=1)# mpu6050 tempdef read_byte(reg):  return i2c.readfrom_mem(i2c_addr, reg, 1)  def read_temp():  high = read_byte(0x41)  low = read_byte(0x42)  temp = (high[0]<<8)+low[0];  if (temp > 0x8000):    temp = -((65535-temp)+1)  return (temp/340+36.53)# Received messages from subscriptions will be delivered to this callbackdef sub_cb(topic, msg):    print((topic, msg))    if msg==b'off':      p2.value(1)    elif msg==b'on':      p2.value(0)        aa=OneNetMQTT('28368380','127931','oCK5MpA0TVP6E=Um8fYVe3zjack=',sub_cb)msg={"Temperature":26.4,"switch_pc":0}aa.start_heartbeat(1000)i2c=I2C(scl=Pin(14),sda=Pin(2),freq=100000)#wdt=WDT(timeout=5000)while True:  #msg['Temperature']=read_temp()  msg['switch_pc']=p2.value()  #print(msg)  aa.publish3(msg)  #aa.publish3('{\"datastreams\":[{\"id\":\"Temperature\",\"datapoints\":[{\"value\":'+str(25.4)+'}]}]}')  time.sleep(3)  #wdt.feed()